mapper.select:
select count(*)
from exam_student
where exam_id=#{exam_id} and student_id=#{student_id}

select id, student_id, exam_id, starttime, endtime, student_score
from exam_student
where exam_id=#{exam_id} and student_id=#{student_id}

mapper.update:
update exam_student 
set student_score=#{student_score}, endtime=now()
where id = #{id}
